                {
                    validator: checkLength(32),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "用户密码",
            id: "userPasswd",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(32),
                    trigger: ["change", "blur"],
