class FileOrder extends React.PureComponent {
    columns = (params) => {
        return [
            {
                title: "用户ID",
                dataIndex: "userId",
            },
            {
                title: "文件名",
                dataIndex: "fileName",
            },
            {
                title: "创建时间",
                dataIndex: "createTime",
            },
            {
                title: "错误信息",
                dataIndex: "errCode",
            },
            {
                title: "下载",
                key: "operation",
                fixed: "right",
                width: 100,
                render: (text, record) => (
                    <a
                        onClick={(e) => {
                            this.handleUpdateBtn(record);
                        }}
                    >
                        下载
                    </a>
                ),
            },
        ];
    };

    state = {
        searchLoading: false,
        selectedRowKeys: [],
        info: [],
        updateArr: [],
        updateModalVisible: false,
    };

    getCounterInfo = (params = {}) => {
        // params.token = "";
        // console.log(this.record);
        params.businessType = this.record.businessType;
        http.post({
            url: "/counter-info/listAll",
            data: params,
        })
            .then((res) => {
                console.log("柜台信息", res);
                //解析数据字典
                if (res.data.length > 0) {
                    parseDict(res.data);
                    // // showTip(this);
                    this.setState(
                        {
                            updateModalVisible: true,
                            selectedRowKeys: [this.record.counterGwId],
                        },
                        () => {
                            this.setState(
                                {
                                    updateArr: res.data,
                                },
                                () => {
                                    this.refs.counterTable.querySelector(
                                        ".ant-table-body"
                                    ).style.height = "600px";
                                }
                            );
                        }
                    );
                } else {
                    message.info("柜台用户信息查询结果为空");
                }
            })
            .catch((e) => {
                message.error("柜台用户信息查询失败");
            });
    };
    // 下载按钮点击事件
    handleUpdateBtn = (record) => {
        console.log("下载按钮", record);
        this.record = record;

        this.getCounterInfo();
    };

    getData = (params = {}, pagination = { current: 1, pageSize: 11 }) => {
        params = {
            ...params,
            pageId: pagination.current,
            pageNum: pagination.pageSize,
        };
        // params.token = "";
        http.post({
            url: "/counter-user-info/list",
            data: params,
        }).then((res) => {
            console.log(res);
            //解析数据字典
            if (res.data.records && res.data.records.length > 0) {
                // parseDict(res.data);
                parseDictValue(res.data.records);
                // showTip(this);
            } else {
                message.info("查询结果为空");
            }
            let pgn = {
                current: res.data.current,
                pageSize: pagination.pageSize,
                total: res.data.total || 0,
            };
            this.setState({
                info: res.data.records,
                pagination: pgn,
            });
        });
    };
    handleSearch = (params, pagination) => {
        this.searchParam = params;
        this.getData(params, pagination);
    };
    componentDidMount() {
        this.getData();
    }
    render() {
        let scroll = { x: 1000, y: 445 };
        let info = this.state.info;

        return (
            <div>
                <CurdComponent
                    // rowKey={"index"}
                    // btnText2="查全部"
                    // isShowSearchForm={false}
                    onSearchClick={this.handleSearch}
                    getSearchFormFields={getSearchFormFields}
                    // searchLoading={this.state.searchLoading}
                    // insertBtnText={"新增"} // 不传 就没新增按钮
                    getInsertFormFields={getInsertFormFields}
                    // insertRecord={this.handleInsertRecord}
                    // col="2"
                    width="600px"
                    pagination={this.state.pagination}
                    // updateModalText="修改柜台网关Id"
                    getUpdateFormFields={getUpdateFormFields}
                    // setUpdateModal={this.setUpdateModal}
                    // updateRecord={this.handleUpdateRecord} // 不传 就没编辑
                    // deleteRecord={this.handleDeleteRecord} // 不传 就没删除
                    centered={true}
                    columns={this.columns}
                    dataSource={info}
                    scroll={scroll}
                >
                    <div
                        urlPrefix="/counter-user-info"
                        title="柜台用户"
                        sucCallback={this.getData}
                    ></div>
                </CurdComponent>
            </div>
        );
    }
}
export default Form.create()(FileOrder);
