import React from "react";
import CurdComponent from "@/components/CurdComponent";
import SelectOption from "@/components/SelectOption";
import { Input } from "antd";

const columns = (params) => {
    return [
        {
            title: "用户Id",
            dataIndex: "uuserId",
        },
        {
            title: "市场代码",
            dataIndex: "market",
        },
        {
            title: "证券代码",
            dataIndex: "securityId",
            key: "securityId",
        },
        {
            title: "信用标识",
            dataIndex: "creditType",
            width: 190,
        },
        {
            title: "最大可融券数量",
            dataIndex: "leaveQty",
            width: 140,
        },
        {
            title: "当前融券数量",
            dataIndex: "shortQty",
            width: 125,
        },
        {
            title: "当前可还券数量",
            dataIndex: "repayStock",
            width: 140,
        },
        {
            title: "当前已还券数量",
            dataIndex: "payedQty",
            width: 140,
        },
        {
            title: "融资保证金比例",
            dataIndex: "marginLongRatio",
            width: 150,
        },
        {
            title: "融券保证金比例",
            dataIndex: "marginShortRatio",
            width: 150,
        },
        {
            title: "折算率",
            dataIndex: "convertRatio",
        },
        {
            title: "创建时间",
            dataIndex: "createTime",
            width: 180,
        },
        {
            title: "更新时间",
            dataIndex: "updateTime",
            key: "updateTime",
            width: 180,
        },
    ];
};
let typeArr = [
    { key: "", value: "全部" },
    { key: 8, value: "新股" },
];

let getSearchFormFields = () => {
    return [
        {
            label: "证券代码",
            id: "securityId",
            component: <Input placeholder="请输入" />,
        },
        {
            label: "交易类型",
            id: "creditType",
            initialValue: "",
            component: SelectOption(typeArr, {
                allowClear: true,
                placeholder: "请选择交易类型",
            }),
        },
    ];
};
const getInsertFormFields = () => {
    return [
        {
            label: "证券代码",
            id: "securityId",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(8),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "证券代码源",
            id: "securityIdSource",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(4),
                    trigger: ["change", "blur"],
                },
            ],
            component: (
                // <Input placeholder="请输入" readOnly disabled />
                <Input placeholder="请输入" />
            ),
        },
        {
            label: "证券名称",
            id: "securityName",
            initialValue: "",
            rules: [
