import React from "react";
import styles from "./style.module.less";
import echarts from "echarts";
import {
    SearchForm,
    Input,
    TimePicker,
    Form,
    Button,
    Icon,
    DatePicker,
    Select,
    AutoComplete,
} from "antd";
import { connect } from "react-redux";

const { RangePicker } = DatePicker;
const { Option } = Select;
// import moment from "moment";
// import { lte } from "semver";
// import { tuple } from "antd/lib/_util/type";
class AlgorithmicTrad extends React.PureComponent {
    state = {
        numberText: "笔",
        securityList: [],
        algoList: [],
        userList: [],
    };
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log(values);
                let noTime = values["pickerTime"].length < 1;
                let params = {
                    securityId: values.securityId,
                    algorithmId: values.algorithmId,
                    uuserId: values.uuserId,
                    countWay: values.countWay,
                    startTime: noTime
                        ? ""
                        : Date.parse(values["pickerTime"][0]) * 1000,
                    endTime: noTime
                        ? ""
                        : Date.parse(values["pickerTime"][1]) * 1000,
                };
                this.getData(params);
                this.setState({
                    numberText: values.countWay == "0" ? "笔" : "元",
                });
            }
        });
    };
    getData = (params, count = 0) => {
        http.post({
            url: "/statistics/algo",
            data: params,
        }).then((res) => {
            if (res.code == 0) {
                let data = res.data;
                let echartLen = Object.keys(data);
                if (
                    !data["algoOrder"].length &&
                    !data["childOrder"].length &&
                    !data["dealAlgoOrder"].length
                ) {
                    message.error("该时间段暂无数据");
                }
                echartLen.forEach((item) => {
                    this.generateChart(data[item], item);
                });
            } else {
                message.error("服务异常");
            }
        });
    };
    generateChart = (list, type) => {
        if (list.length == 1) {
            list.push({ x: "", y: list[0].y });
        }
        let lineObj = {
            algoOrder: { name: "母单信息", color: "#F78B7F" },
            childOrder: { name: "子单信息", color: "#FFD747" },
            dealAlgoOrder: { name: "成交信息", color: "#65D2FF" },
        };
        let isNull = list.length ? false : true;
        let option = {
            title: {
                top: "12px",
                left: "32px",
                text: "{a|}  " + lineObj[type].name,
                textStyle: {
                    color: "#333333",
                    fontSize: 14,
                    fontWeight: 500,
                    rich: {
                        a: {
                            backgroundColor: "#3281FF",
                            height: 10,
                            width: 2,
                        },
                    },
                },
            },
            textStyle: {
                color: "#333",
            },
            tooltip: {
                trigger: "axis",
                backgroundColor: "#1F2329",
                boxShadow: "0px 2px 8px 0px rgba(0, 0, 0, 0.15)",
                borderColor: "#1F2329",
                textStyle: {
                    color: "#fff",
                },
            },
            dataset: {
                dimensions: ["x", "y"],
                source: list,
            },
            grid: {
                // left: isNull ? 15 : "0px",
                right: "50px",
                bottom: "24px",
                top: "75px",
                containLabel: true,
            },
            xAxis: {
                type: "category",
                boundaryGap: false,
                splitLine: {
                    show: true,
                    lineStyle: {
                        color: "#E9E9E9",
                        type: "dashed",
                    },
                },
                axisLabel: {
                    // interval: 0,
                    // rotate: 30,
                },
                axisTick: {
                    show: true, //显示X轴刻度
                    lineStyle: {
                        color: "#E9E9E9",
                    },
                },
                axisLine: {
                    // 刻度线的颜色
                    show: false,
                },
                axisPointer: {
                    type: "line",
                    lineStyle: { color: "#BDBEBF" },
                },
            },
            yAxis: [
                {
                    type: "value",
                    name: `单位：(${this.state.numberText})`,
                    nameLocation: "end",
                    axisLine: {
                        show: false,
                    },
                    nameTextStyle: {
                        color: "#666",
                    },
                    axisTick: {
                        show: false, //隐藏X轴刻度
                    },
                    splitLine: {
                        show: true,
                        lineStyle: {
                            color: "#E9E9E9",
                            type: "dashed",
                        },
                    },
                    nameTextStyle: {
                        align: "center",
                        padding: [0, 43, 0, 20],
                    },
                    min: isNull ? 0 : null,
                    max: isNull ? 100 : null,
                    axisLabel: {
                        margin: 20,
                        // width: 200,
                        // padding: 55,
                        textStyle: {
                            align: "right",
                            // width: 55,
                            // padding: 55,
                        },
                    },
                    nameTextStyle: {
                        padding: [0, 0, 0, -40],
                        // padding: [0, 0, 0, -250],
                    },
                },
            ],
            series: [
                {
                    name: "算法单数",
                    type: "line",
                    smooth: true,
                    showSymbol: false,
                    // symbol: "circle",
                    itemStyle: {
                        normal: {
                            color: lineObj[type].color,
                        },
                    },
                    areaStyle: {
                        normal: {
                            color: new echarts.graphic.LinearGradient(
                                0,
                                0,
                                0,
                                1,
                                [
                                    {
                                        offset: 0,
                                        color: lineObj[type].color,
                                    },
                                    {
                                        offset: 1,
                                        color: "rgba(255,255,255,0)",
                                    },
                                ],
                                false
                            ),
                            shadowColor: "rgba(0, 0, 0, 0.1)",
                            shadowBlur: 10,
                        },
                    },
                },
            ],
        };
        var myChart = echarts.init(document.getElementById(type));
        myChart.setOption(option);
        myChart.resize();
    };
    chartResize = () => {
        let domList2 = ["algoOrder", "childOrder", "dealAlgoOrder"];
        domList2.forEach((item) => {
            echarts.init(document.getElementById(item)).resize();
        });
    };

    componentDidMount() {
        this.getData({
            securityId: "",
            algorithmId: "",
            uuserId: "",
            startTime: "",
            endTime: "",
            countWay: "0",
        });
        this.getSelectList();
    }
    getSelectList = () => {
        //证券
        // http.get({
        //     url: "security/listAll",
        // }).then((res1) => {
        // 算法
        http.get({
            url: "/algo/listAll",
        }).then((res2) => {
            // 用户
            http.get({
                url: "/user/listAll",
            }).then((res3) => {
                this.setState({
                    // securityList: res1.data,
                    algoList: res2.data,
                    userList: res3.data,
                });
            });
        });
        // });
    };

    render() {
        console.log(this.props.path);
        window.cpuResize = this.chartResize;
        if (this.props.path == "/main/tradeStatistics/algorithmicTrad") {
            window.addEventListener("resize", window.cpuResize);
        } else {
            window.removeEventListener("resize", window.cpuResize);
        }
        const { algoList, userList, securityList } = this.state;
        const children = algoList.map((d) => (
            <Option key={d.id}>{d.algoName}</Option>
        ));
        const children2 = userList.map((d) => (
            <Option key={d.id}>{d.userName}</Option>
        ));
        // const children3 = securityList.map((d) => (
        //     <Option key={d.securityId}>{d.securityName}</Option>
        // ));

        const { getFieldDecorator } = this.props.form;
        return (
            <div className={styles.container}>
                <div className={styles.search}>
                    <Form layout="inline" onSubmit={this.handleSubmit}>
                        <Form.Item label="交易类型">
                            {getFieldDecorator("countWay", {
                                initialValue: "0",
                            })(
                                <Select
                                    showSearch
                                    style={{ width: 160 }}
                                    placeholder="选择单位"
                                    optionFilterProp="children"
                                    filterOption={(input, option) =>
                                        option.props.children
                                            .toLowerCase()
                                            .indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    <Option value="0">笔数</Option>
                                    <Option value="1">金额</Option>
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item
                            label="证券代码"
                            style={{ marginLeft: "12px" }}
                        >
                            {getFieldDecorator("securityId", {
                                initialValue: "",
                            })(
                                <Input placeholder="请输入证券代码" />
                                // <Select
                                //     placeholder="请输入算法ID"
                                //     showSearch
                                //     style={{ width: 160 }}
                                //     optionFilterProp="children"
                                //     filterOption={(input, option) =>
                                //         option.props.children
                                //             .toLowerCase()
                                //             .indexOf(input.toLowerCase()) >= 0
                                //     }
                                // >
                                //     {children3}
                                // </Select>
                            )}
                        </Form.Item>
                        <Form.Item
                            label="算法ID"
                            style={{ marginLeft: "12px" }}
                        >
                            {getFieldDecorator("algorithmId")(
                                // <Input placeholder="请输入算法ID" />

                                <Select
                                    allowClear={true}
                                    placeholder="请输入算法ID"
                                    showSearch
                                    style={{ width: 160 }}
                                    optionFilterProp="children"
                                    filterOption={(input, option) =>
                                        option.props.children
                                            .toLowerCase()
                                            .indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    {children}
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item
                            label="用户名称"
                            style={{ marginLeft: "12px" }}
                        >
                            {getFieldDecorator("uuserId")(
                                // <Input placeholder="请输入用户名" />
                                <Select
                                    allowClear={true}
                                    showSearch
                                    style={{ width: 160 }}
                                    placeholder="请输入用户名"
                                    optionFilterProp="children"
                                    filterOption={(input, option) =>
                                        option.props.children
                                            .toLowerCase()
                                            .indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    {children2}
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item label="日期" style={{ marginLeft: "12px" }}>
                            {getFieldDecorator("pickerTime", {
                                initialValue: [],
                            })(
                                <RangePicker
                                    style={{ width: 380 }}
                                    showTime
                                    format="YYYY-MM-DD HH:mm:ss"
                                />
                            )}
                        </Form.Item>
                        <Form.Item style={{ float: "right" }}>
                            <Button
                                type="ghost"
                                htmlType="submit"
                                style={{ width: 76 }}
                            >
                                确定
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
                <div className={styles.chart}>
                    <div id="algoOrder" className={styles.chartBox}></div>
                    <div id="childOrder" className={styles.chartBox}></div>
                    <div id="dealAlgoOrder" className={styles.chartBox}></div>
                    {/* <div
                        id="main_1"
                        style={{ width: "48%", height: "500px", float: "left" }}
                    ></div>
                    <div
                        id="main_2"
                        style={{
                            width: "48%",
                            height: "500px",
                            float: "right",
                        }}
                    ></div> */}
                </div>
            </div>
        );
    }
}
const mapStateToProps = (state, ownProps) => {
    return {
        path: state.RouterModel.path,
    };
};
export default connect(mapStateToProps, null)(Form.create()(AlgorithmicTrad));
