import React from "react";
import CurdComponent from "@/components/CurdComponent";
import SelectOption from "@/components/SelectOption";
import { Input, Modal, Form, message, Icon, Tooltip } from "antd";
import Table from "@/components/Table";

let getSearchFormFields = () => {
    return [
        {
            label: "文件类型",
            id: "type",
            component: SelectOption(dict.fileTypeList, {
                placeholder: "请选择文件类型",
                allowClear: true,
                // onChange: this.inputChange,
            }),
        },
        {
            label: "用户ID",
            id: "userId",
            component: <Input placeholder="请输入用户ID" />,
        },
    ];
};

class FileOrder extends React.PureComponent {
    columns = (params) => {
        return [
            {
                title: "用户ID",
                dataIndex: "userId",
                width: 150,
            },
            {
                title: "文件名",
                dataIndex: "fileName",
            },
            {
                title: "创建时间",
                dataIndex: "updateTime",
                width: 220,
            },
            {
                title: "错误信息",
                dataIndex: "errCode",
            },
            {
                title: "下载",
                key: "operation",
                fixed: "right",
                width: 100,
                render: (text, record) =>
                    record.fileType == 0 ? null : (
                        <a
                            onClick={(e) => {
                                this.handleUpdateBtn(record);
                            }}
                        >
                            下载
                        </a>
                    ),
            },
        ];
    };

    state = {
        searchLoading: false,
        selectedRowKeys: [],
        info: [],
        updateArr: [],
        updateModalVisible: false,
        // fileTypeList: [],
    };

    // 下载按钮点击事件
    handleUpdateBtn = (record) => {
        console.log("下载按钮", record);
        this.record = record;
        let fileName = record.fileName.replace(/\\/g, "%");
        let filePath = record.filePath.replace(/\\/g, "%");
        // let downFilePath = `${window.baseURL}/file-scan/downloadFileScan?fileName=${fileName}&filePath=${filePath}`;

        window.location.href = downFilePath;
        console.log(downFilePath);
        // window.baseURL + this.props.urlPrefix + "/download";
        // window.location.href =
        //     "http://192.168.2.105:20010/file-scan/downloadFileScan?fileName=2022_aUser0000085_order.csv&filePath=group1%2FM00%2F00%2F00%2FwKgBVWMXE2qABX6yAAAB7gEuZ_c866.csv";
        this.getDownLoadFile(record);
    };
    getDownLoadFile = (record) => {
        let params = {
            fileName: record.fileName,
            filePath: record.filePath,
        };
        http.get({
            url: "/file-scan/downloadFileScan",
            data: params,
        })
            .then((res) => {
                let blob = new Blob([res], {
                    type: "application/vnd.ms-excel", // 这边的类型需要改
                });
                let url = window.URL.createObjectURL(blob);
                let link = document.createElement("a");
                link.style.display = "none";
                link.download = record.fileName; //  这边的名字需要改
                link.href = url;
                document.body.appendChild(link);
                link.click();
            })
            .catch((e) => {
                message.error("下载失败");
            });
    };
    getData = (params = {}, pagination = { current: 1, pageSize: 11 }) => {
        params = {
            ...params,
            pageId: pagination.current,
            pageNum: pagination.pageSize,
        };
        http.post({
            url: "/file-scan/pageList",
            data: params,
        }).then((res) => {
            console.log(res);
            //解析数据字典
            if (res.data.records && res.data.records.length > 0) {
                parseArrDict(res.data.records, "errCode", "retCode");
            } else {
                message.info("查询结果为空");
            }
            let pgn = {
                current: res.data.current,
                pageSize: pagination.pageSize,
                total: res.data.total || 0,
            };
            this.setState({
                info: res.data.records,
                pagination: pgn,
            });
        });
    };
    handleSearch = (params, pagination) => {
        this.searchParam = params;
        console.log(params, "params");
        this.getData(params, pagination);
    };
    componentDidMount() {
        this.getData();
    }
    render() {
        let scroll = { x: 1000, y: 445 };
        let info = this.state.info;

        return (
            <div>
                <CurdComponent
                    onSearchClick={this.handleSearch}
                    getSearchFormFields={getSearchFormFields}
                    width="600px"
                    pagination={this.state.pagination}
                    centered={true}
                    columns={this.columns}
                    dataSource={info}
                    scroll={scroll}
                >
                    <div
                        upLoadCSvUrl="/file-scan/uploadFile"
                        noDownload={true}
                        title="文件"
                        sucCallback={this.getData}
                        type=".csv"
                    ></div>
                </CurdComponent>
            </div>
        );
    }
}
export default Form.create()(FileOrder);
