import React from "react";
import CurdComponent from "@/components/CurdComponent";
import SelectOption from "@/components/SelectOption";
import { Input, Modal, Radio, Form, Button, Switch, Col } from "antd";
import styles from "./style.module.less";
import { connect } from "react-redux";

let getSearchFormFields = () => {
    return [
        {
            label: "风控组ID",
            label: <span>风&nbsp;控&nbsp;组&nbsp;ID</span>,
            id: "id",
            component: <Input placeholder="请输入" />,
        },
        {
            label: "风控组名称",
            id: "riskName",
            component: <Input placeholder="请输入" />,
        },
    ];
};

class riskConfigManage extends React.PureComponent {
    state = {
        searchLoading: false,
        selectRow: [],
        info: [],
        updateModalVisible: false,
        userRiskConfig: {},
        disabled0: false,
        disabled1: false,
        disabled2: false,
        disabled3: false,
        disabled4: false,
        disabled5: false,
        disabled6: false,
        disabled7: false,
        disabled8: false,
        configShow: false, // 风控是否显示
    };
    columns = (params) => {
        let arr = [
            {
                title: "风控组ID",
                dataIndex: "Id",
                width: 100,
            },
            {
                title: "风控组名称",
                dataIndex: "RiskName",
                width: 150,
            },
            {
                title: "风控类型",
                dataIndex: "RiskTypeValue",
                width: 100,
            },
            {
                title: "阈值（总委比）",
                dataIndex: "EntrustItemThreshold",
                width: 200,
            },
            {
                title: "笔数（总委比）",
                dataIndex: "EntrustItemLimit",
                width: 200,
            },
            {
                title: "时间（总委比）(s)",
                dataIndex: "EntrustSeconds",
                width: 165,
            },
            {
                title: "总委托笔数",
                dataIndex: "EntrustTotalThreshold",
                key: "EntrustTotalThreshold",
            },
            {
                title: "阈值（撤单比）",
                dataIndex: "CancelEntrustItemThreshold",
                key: "CancelEntrustItemThreshold",
                width: 200,
            },
            {
                title: "撤单比(%) ",
                dataIndex: "CancelRatioLimit",
                width: 110,
            },
            {
                title: "阈值（废单比）",
                dataIndex: "FailedEntrustItemThreshold",
                key: "FailedEntrustItemThreshold",
                width: 200,
            },
            {
                title: "废单比(%) ",
                dataIndex: "FailedRatioLimit",
                key: "FailedRatioLimit",
            },
            {
                title: "阈值（委托成交比）",
                dataIndex: "EntrustExecEntrustItemThreshold",
                key: "EntrustExecEntrustItemThreshold",
                width: 240,
            },
            {
                title: "委托成交比(%) ",
                dataIndex: "EntrustExecRatioLimit",
                key: "EntrustExecRatioLimit",
            },
            {
                title: "阈值（净买入）",
                dataIndex: "NetBuyEntrustItemThreshold",
                key: "NetBuyEntrustItemThreshold",
                width: 240,
            },
            {
                title: "净买入额度",
                dataIndex: "NetBuyAmountLimit",
                key: "NetBuyAmountLimit",
            },
            {
                title: "撤单频率笔数",
                dataIndex: "CancelItemLimit",
                key: "CancelItemLimit",
            },
            {
                title: "撤单:时间量(s)",
                dataIndex: "CancelSeconds",
                key: "CancelSeconds",
            },
            {
                title: "撤单间隔(s)",
                dataIndex: "CancelGapSeconds",
                key: "CancelGapSeconds",
            },
            {
                title: "下单频率笔数",
                dataIndex: "TradeItemLimit",
                key: "TradeItemLimit",
            },
            {
                title: "下单频率:时间量(s)",
                dataIndex: "TradeSeconds",
                key: "TradeSeconds",
            },
            {
                title: "下单总量(股数)",
                dataIndex: "TradeQtyLimit",
                key: "TradeQtyLimit",
            },
            {
                title: "下单总金额(元)",
                dataIndex: "TradeAmountLimit",
                key: "TradeAmountLimit",
            },
        ];
        if (this.authObj.isUpdate) {
            arr.push({
                title: "操作",
                key: "operation",
                fixed: "right",
                width: 100,
                render: (text, record) => (
                    <a
                        onClick={(e) => {
                            this.handleUpdateBtn(record);
                        }}
                    >
                        编辑
                    </a>
                ),
            });
        }
        return arr;
    };
    // 新增按钮点击事件
    handleInsertBtn = (params) => {
        this.isInsert = true;
        this.isUpdate = false;
        this.setState({
            updateModalVisible: true,
        });
        this.props.form.resetFields();
    };
    // 编辑按钮点击事件
    handleUpdateBtn = (record) => {
        console.log("更新记录", record);
        this.record = record;
        this.isInsert = false;
        this.isUpdate = true;
        this.typeChange(record.RiskType);
        this.setState(
            {
                updateModalVisible: true,
                disabled0: false,
                disabled1: false,
                disabled2: false,
                disabled3: false,
                disabled4: false,
                disabled5: false,
                disabled6: false,
                disabled7: false,
                disabled8: false,
            },
            () => {
                let config = record;
                let enable = (record.RiskEnable / 1)
                    .toString(2)
                    .padStart(9, "0");
                let enableArr = enable.split("").map((item) => item == 1);
                // console.log(enableArr);
                // return;
                this.props.form.setFieldsValue({
                    // riskGroup: record.riskGroup + "",
                    byte0: enableArr[8],
                    byte1: enableArr[7],
                    byte2: enableArr[6],
                    byte3: enableArr[5],
                    byte4: enableArr[4],
                    byte5: enableArr[3],
                    byte6: enableArr[2],
                    byte7: enableArr[1],
                    byte8: enableArr[0],
                    riskName: config.RiskName,
                    riskType: config.RiskType + "",
                    entrustItemThreshold: config.EntrustItemThreshold,
                    entrustItemLimit: config.EntrustItemLimit,
                    entrustSeconds: config.EntrustSeconds,
                    entrustTotalThreshold: config.EntrustTotalThreshold,
                    cancelEntrustItemThreshold:
                        config.CancelEntrustItemThreshold,
                    cancelRatioLimit: config.CancelRatioLimit,
                    failedEntrustItemThreshold:
                        config.FailedEntrustItemThreshold,
                    failedRatioLimit: config.FailedRatioLimit,
                    entrustExecEntrustItemThreshold:
                        config.EntrustExecEntrustItemThreshold,
                    entrustExecRatioLimit: config.EntrustExecRatioLimit,
                    netBuyEntrustItemThreshold:
                        config.NetBuyEntrustItemThreshold,
                    netBuyAmountLimit: config.NetBuyAmountLimit,
                    cancelItemLimit: config.CancelItemLimit,
                    cancelSeconds: config.CancelSeconds,
                    cancelGapSeconds: config.CancelGapSeconds,
                    tradeItemLimit: config.TradeItemLimit,
                    tradeSeconds: config.TradeSeconds,
                    tradeQtyLimit: config.TradeQtyLimit,
                    tradeAmountLimit: config.TradeAmountLimit,
                });
                // this.props.form.setFieldsValue({
                //     riskGroup: record.riskGroup + "",
                //     userName: record.userName,
                // });
            }
        );
    };

    //弹窗确定
    handleUpdateModalOk = () => {
        this.props.form.validateFields((err) => {
            if (!err) {
                let data = this.props.form.getFieldsValue();
                // console.log(data);
                if (this.isInsert) {
                    console.log("新增风控");
                    this.addRiskGroup(data);
                } else {
                    console.log("修改风控");
                    this.updateRiskGroup(data);
                }
            } else {
                let data = this.props.form.getFieldsValue();
                let errKey = Object.keys(err);
                // console.log(errKey);
                if (data.riskType == 2) {
                    let notOk =
                        errKey.includes("entrustTotalThreshold") ||
                        errKey.includes("cancelEntrustItemThreshold") ||
                        errKey.includes("cancelRatioLimit") ||
                        errKey.includes("failedEntrustItemThreshold") ||
                        errKey.includes("failedRatioLimit") ||
                        errKey.includes("netBuyEntrustItemThreshold") ||
                        errKey.includes("failedRatioLimit") ||
                        errKey.includes("cancelGapSeconds");
                    // console.log("校验不通过 ", notOk);
                    if (!notOk) {
                        if (this.isInsert) {
                            console.log("新增风控");
                            this.addRiskGroup(data);
                        } else {
                            console.log("修改风控");
                            this.updateRiskGroup(data);
                        }
                    }
                    return;
                }
                message.error("输入内容不正确,请完善");
            }
        });
    };
    handleUpdateModalCancel = () => {
        this.setState({
            updateModalVisible: false,
        });
    };

    //新增风控组
    addRiskGroup = (data) => {
        for (let i = 0; i <= 8; i++) {
            let key = "byte" + i;
            if (data[key]) {
                data[key] = 1;
            } else {
                data[key] = 0;
            }
        }
        data.riskEnable =
            ("0b" +
                data.byte8 +
                data.byte7 +
                data.byte6 +
                data.byte5 +
                data.byte4 +
                data.byte3 +
                data.byte2 +
                data.byte1 +
                data.byte0) /
                1 +
            "";
        // let params = {
        //     userId: data.userId,
        //     riskGroup: data.riskGroup / 1,
        // };
        console.log(data);
        // return;
        http.post({
            url: "/risk/addRisk",
            data: data,
        }).then((res) => {
            let msg = res.message;
            if (res.code == 0) {
                message.success(msg);
                this.setState({
                    updateModalVisible: false,
                });
                this.getData();
            } else if (res.code == 20000) {
                message.error(
                    msg.substring(msg.indexOf("[") + 1, msg.lastIndexOf("]"))
                );
            } else {
                message.error(msg);
            }
        });
    };
    //修改风控组
    updateRiskGroup = (data) => {
        // console.log(data);
        let enable =
            ("0b" +
                Number(data.byte8) +
                Number(data.byte7) +
                Number(data.byte6) +
                Number(data.byte5) +
                Number(data.byte4) +
                Number(data.byte3) +
                Number(data.byte2) +
                Number(data.byte1) +
                Number(data.byte0)) /
            1;
        // console.log(enable);
        // return enable;
        let riskArr = [
            {
                RiskType: "0", // 修改风控名
                RadioSecond: "0",
                Threshold: "0",
                TradeLimit: "0",
                TradeAmount: "0",
                RiskName: data.riskName,
            },
            {
                RiskType: "1", // 修改风控使能
                RadioSecond: enable + "",
                Threshold: "0",
                TradeLimit: "0",
                TradeAmount: "0",
                RiskName: "",
            },
            {
                RiskType: "2", // 风控类型:时间量总委托笔数 (bit0)
                RadioSecond: data.entrustSeconds + "", // 总委托:时间量(s)
                Threshold: data.entrustItemThreshold + "", // 风控启用委托数量
                TradeLimit: data.entrustItemLimit + "", // 时间量:总委托笔数
                TradeAmount: "0",
                RiskName: "",
            },
            {
                RiskType: "3", // 风控类型:总委托笔数 (bit1)
                RadioSecond: "0",
                Threshold: data.entrustTotalThreshold + "", // 总委托笔数,客户委托总数不超过总委托笔数
                TradeLimit: "0",
                TradeAmount: "0",
                RiskName: "",
            },
            {
                RiskType: "4", // 风控类型:撤单比 (bit2)
                RadioSecond: data.cancelRatioLimit + "", // 撤单比(s)
                Threshold: data.cancelEntrustItemThreshold + "", // 撤单比:风控启用委托数量
                TradeLimit: "0",
                TradeAmount: "0",
                RiskName: "",
            },
            {
                RiskType: "5", // 风控类型:废单比 (bit3)
                RadioSecond: data.failedRatioLimit + "", // 废单比(s)
                Threshold: data.failedEntrustItemThreshold + "", // 废单比:风控启用委托数量
                TradeLimit: "0",
                TradeAmount: "0",
                RiskName: "",
            },
            {
                RiskType: "6", // 风控类型:委托成交比 (bit4)
                RadioSecond: data.entrustExecRatioLimit + "", // 委托成交比(s) , 仅显示
                Threshold: data.entrustExecEntrustItemThreshold + "", // 委托成交比:风控启用委托数量
                TradeLimit: "0",
                TradeAmount: "0",
                RiskName: "",
            },
            {
                RiskType: "7", // 风控类型:净买入额度 (bit5)
                RadioSecond: "0",
                Threshold: data.netBuyEntrustItemThreshold + "", // 净买入额度:风控启用委托数量
                TradeLimit: data.netBuyAmountLimit * 10000 + "", // 净买入额度
                TradeAmount: "0",
                RiskName: "",
            },
            {
                RiskType: "8", // 风控类型:撤单频率 (bit6)
                RadioSecond: data.cancelSeconds + "", // 撤单:时间量(s)
                Threshold: "0",
                TradeLimit: data.cancelItemLimit + "", // 撤单频率笔数
                TradeAmount: "0",
                RiskName: "",
            },
            {
                RiskType: "9", // 风控类型:同笔撤单间隔 (bit7)
                RadioSecond: data.cancelGapSeconds + "", // 撤单间隔(s)
                Threshold: "0",
                TradeLimit: "0",
                TradeAmount: "0",
                RiskName: "",
            },
            {
                RiskType: "10", // 风控类型:下单频率 (bit8)
                RadioSecond: data.tradeSeconds + "", // 下单频率:时间量(s)
                Threshold: data.tradeQtyLimit + "", // 下单总量(股数)
                TradeLimit: data.tradeItemLimit + "", // 下单频率笔数
                TradeAmount: data.tradeAmountLimit * 10000 + "", // 下单总金额(元)
                RiskName: "",
            },
        ];
        //判断名字 是否改变
        if (data.riskName == this.record.RiskName) {
            riskArr.shift();
        }
        let params = {
            RiskGroup: this.record.Id + "", // 风控组
            RiskPara: riskArr,
        };
        // console.log(params);
        // return;
        http.post({
            url: "/risk/modifyRiskConfig",
            data: params,
        }).then((res) => {
            // console.log(res);
            if (res.code == 0) {
                message.success(res.message);
                this.setState({
                    updateModalVisible: false,
                });
                // this.getData();
                this.handleSearch(window.searchFun());
            } else {
                message.error("风控配置修改失败！");
            }
        });
    };
    //查询风控列表
    getData = (params = {}) => {
        // return;
        http.post({
            // url: "/risk/queryRisk",
            url: "/risk/riskList",
            data: params,
        }).then((res) => {
            console.log(res);
            let arr = [];
            // if (res.data && res.data.length > 0) {
            //     let dataObj = JSON.parse(res.data);
            //     console.log(dataObj);
            //     if (dataObj.TotalCount > 0) {
            //         arr = dataObj.Datas;
            //         // console.log(arr);
            //         // parseDict(arr);
            //         parseDictValue(arr);
            //     }
            // } else {
            //     message.info(res.message || "查询结果为空", 4);
            // }
            if (res.data && res.data.length > 0) {
                let dataArr = res.data;
                if (dataArr.length > 0) {
                    arr = dataArr;
                    let keys = Object.keys(arr[0]);
                    let upperKeys = keys.map((item) => {
                        return item.slice(0, 1).toUpperCase() + item.slice(1);
                    });
                    arr.forEach((item) => {
                        for (let i = 0; i < upperKeys.length; i++) {
                            item[upperKeys[i]] = item[keys[i]];
                        }
                    });
                    // console.log(arr);
                    parseDictValue(arr);
                }
            } else {
                message.info(res.message || "查询结果为空", 4);
            }
            this.setState({
                info: arr,
            });
        });
    };
    getDataByParams = (params = {}) => {
        http.post({
            url: "/risk/queryRiskConfig",
            data: params,
        }).then((res) => {
            console.log(res);
            if (res.code == "20000") {
                message.info("查询结果为空");
                this.setState({
                    info: [],
                });
                return;
            }
            let arr = [];
            if (res.data.length > 0) {
                let dataObj = JSON.parse(res.data);
                arr = [dataObj];
                console.log(arr);
            } else {
                message.info("查询结果为空");
            }
            this.setState({
                info: arr,
            });
        });
    };
    handleSearch = (params, pagination) => {
        params.Id = params.id;
        params.RiskName = params.riskName;
        delete params.id;
        delete params.riskName;
        console.log(params);
        this.getData(params);
        // if (params.id == "" && params.riskName == "") {
        //     // console.log("getData");
        //     this.getData(params, pagination);
        // } else {
        //     // console.log("getDataByParams");
        //     this.getDataByParams(params, pagination);
        // }
    };
    formChange = (idx) => {
        // console.log("formChange!", idx);
        if (this.isInsert) {
            let formData = this.props.form.getFieldsValue();
            let stateKey = "disabled" + idx;
            let formKey = "byte" + idx;
            this.setState({ [stateKey]: formData[formKey] == 1 });
        }
    };
    typeChange = (type) => {
        // console.log("typeChange", type);
        if (type == 1) {
            //用户
            this.setState({
                configShow: true,
            });
        } else {
            //算法
            this.setState({
                configShow: false,
            });
        }
    };
    componentDidMount() {
        this.getData();
    }
    render() {
        let scroll = { x: 4000, y: 445 };
        let info = this.state.info;
        //批量
        // let { selectRow } = this.state;
        // const rowSelection = {
        //     selectRow,
        //     onChange: this.handleTableChange,
        // };
        let { getFieldDecorator } = this.props.form;
        let labelCol = {
            xs: 12,
        };
        let wrapperCol = {
            xs: 24 - labelCol.xs,
        };
        let formItemLayout = {
            labelCol,
            wrapperCol,
        };
        let modalTitle = "新增记录";
        if (this.isUpdate) {
            modalTitle = "修改记录";
        }
        // let pagination = { pageSize: 12 };
        let pageSize = 12;
        // console.log(byte0);
        let cmpt = this.props.activeMenu.cmpt;
        // console.log(cmpt);
        let authObj = {
            isQuery: true,
            isAdd: true,
            isUpload: true,
            isDownload: true,
            isDelete: false,
            isUpdate: true,
        };
        // console.log("cmpt", cmpt);
        if (cmpt) {
            for (let i = 0; i < cmpt.length; i++) {
                let item = cmpt[i];
                // console.log(item);
                if (item.type == 1 && item.auth != 1) {
                    //查询 有权限
                    authObj.isQuery = false;
                }
                if (item.type == 2 && item.auth != 1) {
                    //新增 有权限
                    authObj.isAdd = false;
                }
                if (item.type == 3 && item.auth != 1) {
                    //上传 有权限
                    authObj.isUpload = false;
                }
                if (item.type == 4 && item.auth != 1) {
                    //下载 有权限
                    authObj.isDownload = false;
                }
                // if (item.type == 5 && item.auth != 1) {
                //     //下载报告 有权限 -- 绩效那边
                //     authObj.isExportPdf = false;
                // }
                if (item.type == 6 && item.auth == 1) {
                    //删除 有权限
                    authObj.isDelete = true;
                }
                if (item.type == 7 && item.auth != 1) {
                    //编辑 有权限
                    authObj.isUpdate = false;
                }
            }
        }
        this.authObj = authObj;
        return (
            <div className={styles.userInfo}>
                <CurdComponent
                    rowKey={"Id"}
                    pageSize={pageSize}
                    // isShowSearchForm={false}
                    // btnText2="查全部"
                    onSearchClick={this.handleSearch}
                    getSearchFormFields={getSearchFormFields}
                    // hasSlot={true}
                    // searchLoading={this.state.searchLoading}
                    insertBtnText={"新增风控"} // 不传 就没新增按钮
                    // hasSlot={true}
                    hasSearchSlot={true}
                    addBtn={
                        <Button
                            type="primary"
                            icon="plus"
                            onClick={this.handleInsertBtn}
                        >
                            新增风控
                        </Button>
                    }
                    // getInsertFormFields={getInsertFormFields}
                    // insertRecord={this.handleInsertRecord}
                    // col="2"
                    width="600px"
                    // pagination={pagination}
                    // getUpdateFormFields={getUpdateFormFields}
                    // setUpdateModal={this.setUpdateModal}
                    // updateRecord={this.handleUpdateRecord} // 不传 就没编辑
                    // deleteRecord={this.handleDeleteRecord} // 不传 就没删除
                    centered={true}
                    columns={this.columns}
                    dataSource={info}
                    scroll={scroll}
                    pageSize={11}
                    // rowSelection={rowSelection} //批量选择 操作
                >
                    <div
                        urlPrefix="/risk"
                        title="风控配置"
                        sucCallback={this.getData}
                    ></div>
                </CurdComponent>
                <Modal
                    title={modalTitle}
                    visible={this.state.updateModalVisible}
                    onOk={this.handleUpdateModalOk}
                    onCancel={this.handleUpdateModalCancel}
                    width={788}
                    wrapClassName="riskConfig"
                    centered
                >
                    <Form layout={"vertical"}>
                        <div>
                            <div className={styles.tit}>
