import React from "react";
import styles from "./style.module.less";
import echarts from "echarts";
import moment from "moment";
import {
    SearchForm,
    Input,
    TimePicker,
    Form,
    Button,
    Icon,
    DatePicker,
    Select,
} from "antd";

const { RangePicker } = DatePicker;
const { Option } = Select;
class Cpu extends React.PureComponent {
    state = {
        searchForm: {
            hostId: 0,
            startTime: "",
            endTime: "",
        },
    };
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                console.log(
                    moment(values["range-time-picker"][0]).format(
                        "YYYY/MM/DD HH:mm:ss"
                    )
                );
            }
        });
    };
    getData = (params) => {
        console.log(params, "params");
        http.post({
            data: params,
            url: "/ssh/cpu",
        }).then((res) => {
            console.log(res.data, "请求成功");
            let list = {
                x: ["周一", "周二", "周三", "周四", "周五", "周六", "周日"],
                series: [
                    {
                        name: "邮件营销",
                        data: [120, 132, 101, 134, 90, 230, 210],
                        type: "line",
                    },
                    {
                        name: "联盟广告",
                        data: [220, 182, 191, 234, 290, 330, 310],
                        type: "line",
                    },
                    {
                        name: "视频广告",
                        data: [150, 232, 201, 154, 190, 330, 410],
                        type: "line",
                    },
                    {
                        name: "直接访问",
                        data: [320, 332, 301, 334, 390, 330, 320],
                        type: "line",
                    },
                    {
                        name: "搜索引擎",
                        data: [820, 932, 901, 934, 1290, 1330, 1320],
                        type: "line",
                    },
                ],
            };
            console.log(list.series);
            // let list = res.data;
            if (res.code == 0) {
                let option = {
                    textStyle: {
                        color: "#333",
                    },
                    // title: {
                    //     text: "折线图堆叠",
                    // },
                    tooltip: {
                        trigger: "axis",
                        backgroundColor: "#1F2329",
                        boxShadow: "0px 2px 8px 0px rgba(0, 0, 0, 0.15)",
                        borderColor: "#1F2329",
                        textStyle: {
                            color: "#fff",
                        },
                    },
                    legend: {
                        // data: ["CPU1", "CPU2", "CPU3"],
                        left: 0,
                    },
                    grid: {
                        left: "1%",
                        right: "4%",
                        bottom: "9%",
                        top: "60px",
                        containLabel: true,
                    },
                    xAxis: {
                        type: "category",
                        boundaryGap: false,
                        splitLine: {
                            show: true,
                            lineStyle: {
                                color: "#E9E9E9",
                                type: "dashed",
                            },
                        },
                        axisTick: {
                            show: true, //显示X轴刻度
                            lineStyle: {
                                color: "#E9E9E9",
                            },
                        },
                        axisLine: {
                            // 刻度线的颜色
                            show: false,
                        },
                        axisPointer: {
                            type: "line",
                            lineStyle: { color: "#BDBEBF" },
                        },
                        data: list.x,
                    },
                    yAxis: [
                        {
                            type: "value",
                            name: "单位：（GB）",
                            nameLocation: "end",
                            axisLine: {
                                show: false,
                            },
                            nameTextStyle: {
                                color: "#666",
                            },
                            axisTick: {
                                show: false, //隐藏X轴刻度
                            },
                            splitLine: {
                                show: true,
                                lineStyle: {
                                    color: "#E9E9E9",
                                    type: "dashed",
                                },
                            },
                            axisLabel: {
                                formatter: "{value} GB",
                            },
                            nameTextStyle: {
                                padding: [0, 43, 0, 0],
                            },
                        },
                        {
                            gridIndex: 0,
                            type: "value",
                            name: "单位（%）",
                            splitLine: {
                                show: false,
                            },
                            axisTick: {
                                show: false, //隐藏X轴刻度
                            },
                            axisLine: {
                                show: false,
                            },
                            min: "0",
                            max: "100",
                            axisLabel: {
                                formatter: "{value}%",
                            },
                            nameTextStyle: {
                                padding: [0, 0, 0, 34],
                            },
                        },
                    ],
                    series: list.series,
                    // series: [
                    //     {
                    //         name: "CPU1",
                    //         type: "line",
                    //         smooth: true,
                    //         showSymbol: false,
                    //         itemStyle: {
                    //             normal: {
                    //                 color: "#65A6FF",
                    //             },
                    //         },
                    //     },
                    // ],
                    dataZoom: [
                        {
                            type: "inside",
                        },
                        {
                            type: "slider",
                            height: "20px",
                        },
                    ],
                };
                var myChart = echarts.init(document.getElementById("main1"));
                myChart.setOption(option);
            } else {
                message.error("服务异常");
            }
        });
    };
    componentDidMount() {
        this.getData(this.state.searchForm);
        // this.props.form.setFieldsValue({
        //     hostId: 0,
        //     startTime: "",
        //     endTime: "",
        // });
        echarts.init(document.getElementById("main1"));
    }
    render() {
        const { getFieldDecorator } = this.props.form;
        function onChange(value) {
            console.log(`selected ${value}`);
        }

        function onSearch(val) {
            console.log("search:", val);
        }
        return (
            <div className={styles.container}>
                <div className={styles.search}>
                    <Form layout="inline" onSubmit={this.handleSubmit}>
                        <Form.Item>
                            {getFieldDecorator("hostId", {
                                initialValue: "0",
                            })(
                                <Select
                                    showSearch
                                    style={{ width: 160 }}
                                    placeholder="选择CPU"
                                    optionFilterProp="children"
                                    onChange={onChange}
                                    onSearch={onSearch}
                                    filterOption={(input, option) =>
                                        option.props.children
                                            .toLowerCase()
                                            .indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    <Option value="0">全部</Option>
                                    <Option value="1">CPU1</Option>
                                    <Option value="2">CPU2</Option>
                                    <Option value="3">CPU3</Option>
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item style={{ marginLeft: "12px" }}>
                            {getFieldDecorator("range-time-picker")(
                                <RangePicker
                                    style={{ width: 432 }}
                                    showTime
                                    format="YYYY-MM-DD HH:mm:ss"
                                />
                            )}
                        </Form.Item>
                        <Form.Item style={{ float: "right" }}>
                            <Button
                                type="primary"
                                htmlType="submit"
                                style={{ width: 76 }}
                            >
                                确定
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
                <div>
                    <div
                        id="main1"
                        style={{ width: "100%", height: "500px" }}
                    ></div>
                </div>
            </div>
        );
    }
}
export default Form.create()(Cpu);
