            //             message: "柜台网关Id不能为空",
            //         },
            //         {
            //             validator: checkLength(10),
            //             trigger: ["change", "blur"],
            //         },
            //     ],
            //     component: <Input placeholder="请输入" />,
            // },
            {
                label: "业务类型",
                id: "businessType",
                initialValue: "1",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: SelectOption(dict.businessType, {
                    placeholder: "请选择",
                    allowClear: false,
                    onChange: this.selectChange,
                }),
            },
            {
                label: "柜台网关",
                id: "counterGwId",
                rules: [
                    {
                        required: true,
                        message: "柜台网关不能为空",
                    },
                    // {
                    //     max: 10,
                    //     message: "最大长度为10",
                    // },
                ],
                component: SelectOption(this.state.counterArr, {
                    placeholder: "请选择",
                    allowClear: false,
                }),
            },
            // {
            //     label: "来自柜台",
            //     id: "counterUserId",
            //     rules: [
            //         {
            //             required: true,
            //             message: "参数不能为空",
            //         },
            //         {
            //             validator: checkLength(10),
            //             trigger: ["change", "blur"],
            //         },
            //     ],
            //     component: <Input placeholder="请输入" />,
            // },
            // {
            //     label: "登录状态",
            //     id: "loginStatus",
            //     initialValue: "0",
            //     rules: [
            //         {
            //             required: true,
            //             message: "参数不能为空",
            //         },
            //     ],
            //     component: SelectOption(dict.loginStatus, {
            //         placeholder: "请选择",
            //         allowClear: false,
            //         style: {
            //             width: 400,
            //         },
            //     }),
            // },
            // {
            //     label: "客户类型",
            //     id: "clientType",
            //     initialValue: "0",
            //     rules: [
            //         {
            //             required: true,
            //             message: "参数不能为空",
            //         },
            //     ],
            //     component: SelectOption(dict.clientType, {
            //         placeholder: "请选择",
            //         allowClear: false,
            //         style: {
            //             width: 400,
            //         },
            //     }),
            // },

            {
                label: "终端信息",
                id: "TerminalInfo",
                rules: [
                    // {
                    //     validator: checkLength(256),
                    //     trigger: ["change", "blur"],
                    // },
                    {
                        max: 256,
                        message: "最大长度为256",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "营业编号",
                id: "CustOrgId",
                rules: [
                    // {
                    //     validator: checkLength(256),
                    //     trigger: ["change", "blur"],
                    // },
                    {
                        max: 50,
                        message: "最大长度为50",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "系统编号",
                id: "ClSystemId",
                rules: [
                    // {
                    //     validator: checkLength(256),
                    //     trigger: ["change", "blur"],
                    // },
                    {
                        max: 50,
                        message: "最大长度为50",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
        ];
    };
    getUpdateFormFields = () => {
        return this.getInsertFormFields();
        return [
            {
                label: "用户名称",
                id: "uuserId",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                    {
                        validator: checkLength(10),
                        trigger: ["change", "blur"],
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "柜台用户编码",
                id: "userId",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                    {
                        validator: checkLength(12),
                        trigger: ["change", "blur"],
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "柜台用户名称",
                id: "userName",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                    {
                        validator: checkLength(32),
                        trigger: ["change", "blur"],
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "业务类型",
                id: "businessType",
                initialValue: "1",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: SelectOption(dict.businessType, {
                    placeholder: "请选择",
                    allowClear: false,
                    onChange: this.selectChange,
                }),
            },
            {
                label: "柜台网关",
                id: "counterGwId",
                rules: [
                    {
                        required: true,
                        message: "柜台网关不能为空",
                    },
                    // {
                    //     max: 10,
                    //     message: "最大长度为10",
                    // },
                ],
                component: SelectOption(this.state.counterArr, {
                    placeholder: "请选择",
                    allowClear: false,
                }),
            },
            {
                label: "终端信息",
                id: "TerminalInfo",
                rules: [
                    // {
                    //     validator: checkLength(256),
                    //     trigger: ["change", "blur"],
                    // },
                    {
                        max: 256,
                        message: "最大长度为256",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "营业编号",
                id: "CustOrgId",
                rules: [
                    // {
                    //     validator: checkLength(256),
                    //     trigger: ["change", "blur"],
                    // },
                    {
                        max: 50,
                        message: "最大长度为50",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "系统编号",
                id: "ClSystemId",
                rules: [
                    // {
                    //     validator: checkLength(256),
                    //     trigger: ["change", "blur"],
                    // },
                    {
                        max: 50,
                        message: "最大长度为50",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
        ];
    };
    // let keys = Object.keys(this.insertForm.getFieldsValue()).filter(
    //     (item) => item.includes("counterGwId")
    // );
    // console.log(this.insertForm.getFieldsValue());
    // if (keys.length > 0) {
    //     this.insertForm.resetFields(keys);
    // }
    selectChange = (val) => {
        // console.log("selectChange ", val);
        this.getCounterArr(val);
        if (this.updateForm) {
            this.updateForm.resetFields(["counterGwId"]);
        }
        if (this.insertForm) {
            // 清空柜台地址
            this.insertForm.resetFields(["counterGwId"]);
        }
    };
    columns = (params) => {
        return [
            {
                title: "用户账户",
                dataIndex: "uuserId",
                width: 150,
            },
            {
                title: "用户名称",
                dataIndex: "algoUserName",
                width: 150,
            },
            {
                title: "柜台用户编码",
                dataIndex: "userId",
            },
            {
                title: "柜台用户名称",
                dataIndex: "userName",
            },
            // {
            //     title: "柜台网关Id",
            //     dataIndex: "counterGwId",
            // },
            {
                title: "柜台网关ID",
                dataIndex: "counterGwId",
                render: (text, record) => (
                    <div
                        onClick={(e) => {
                            this.handleUpdateBtn(record);
                        }}
                    >
                        <Tooltip title="修改网关">
                            {record.counterGwId}
                            <Icon type="edit" style={{ color: "#1899ff" }} />
                        </Tooltip>
                    </div>
                ),
            },
            // {
            //     title: "来自柜台",
            //     dataIndex: "counterUserId",
            // },
            {
                title: "业务类型",
                dataIndex: "businessTypeValue",
            },
            {
                title: "登录状态",
                dataIndex: "loginStatusValue",
            },
            // {
            //     title: "客户类型",
            //     dataIndex: "clientTypeValue",
            // },
            {
                title: "终端信息",
                dataIndex: "terminalInfo",
                width: 300,
            },
            {
                title: "营业编号",
                dataIndex: "custOrgid",
                // width: 100,
            },
            {
                title: "系统编号",
                dataIndex: "clSystemId",
                width: 300,
            },
            {
                title: "创建时间",
                dataIndex: "createTime",
                width: 180,
            },
            // {
            //     title: "操作",
            //     key: "operation",
            //     fixed: "right",
            //     width: 100,
            //     render: (text, record) => (
            //         <a
            //             onClick={(e) => {
            //                 this.handleUpdateBtn(record);
            //             }}
            //         >
            //             编辑
            //         </a>
            //     ),
            // },
        ];
    };
