                dataIndex: "brokerName",
                width: 130,
                ellipsis: true,
            },
            {
                title: "支持的业务类型",
                dataIndex: "supportType",
                ellipsis: true,
            },
            {
                title: "柜台地址",
                dataIndex: "gwAddr",
                width: 180,
                ellipsis: true,
            },
            {
                title: "柜台状态",
                dataIndex: "status",
                ellipsis: true,
            },
            {
                title: "柜台版本号",
                dataIndex: "version",
                width: 120,
                ellipsis: true,
            },
        ];
    };

