import React from "react";
import styles from "./style.module.less";
import echarts from "echarts";
import {
    SearchForm,
    Input,
    TimePicker,
    Form,
    Button,
    Icon,
    DatePicker,
    Select,
} from "antd";

const { RangePicker } = DatePicker;
const { Option } = Select;
import moment from "moment";
import { lte } from "semver";
class Ram2 extends React.PureComponent {
    state = {
        echatObj: {},
    };
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                let noTime = values["pickerTime"].length < 1;
                let params = {
                    securityId: values.securityId,
                    algorithmId: values.algorithmId,
                    uuserId: values.uuserId,
                    startTime: noTime
                        ? ""
                        : Date.parse(values["pickerTime"][0]),
                    endTime: noTime ? "" : Date.parse(values["pickerTime"][1]),
                };
                this.getData(params);
            }
        });
    };
    getData = (params, count = 0) => {
        http.post({
            url: "/statistics/algo",
            data: params,
        }).then((res) => {
            if (res.code == 0) {
                // this.setState({
                //     echatObj: res.data,
                // });
                // console.log(this.state.echatObj, 9999);
                let data = {
                    algoOrder: [
                        { x: "1970/01/01 08:00", y: 1 },
                        { x: "2022/06/21 16:46", y: 4 },
                    ],
                    cancelAlgoOrder: [],
                    childOrder: [],
                    dealAlgoOrder: [],
                };
                let echartLen = Object.keys(data);
                echartLen.forEach((item) => {
                    this.generateChart(data[item], item);
                });
            } else {
                message.error("服务异常");
            }
        });
    };
    generateChart = (list, type) => {
        let lineObj = {
            algoOrder: { color: "rgba(50, 129, 255, 0.2)" },
            childOrder: { color: "red" },
            cancelAlgoOrder: { color: "red" },
            dealAlgoOrder: { color: "red" },
        };
        console.log(lineObj[type][color], 999);
        // let option = {
        //     textStyle: {
        //         color: "#333",
        //     },
        //     tooltip: {
        //         trigger: "axis",
        //         backgroundColor: "#1F2329",
