import React from "react";
import CurdComponent from "@/components/CurdComponent";
import SelectOption from "@/components/SelectOption";
// import UploadWrap from "@/components/UploadWrap";
import { Input } from "antd";

// const getUpdateFormFields = () => {
//     return []
// };

const columns = (params) => {
    return [
        {
            title: "用户名称",
            dataIndex: "userName",
            key: "userName",
        },
        {
            title: "股东账户",
            dataIndex: "accountId",
            key: "accountId",
        },
        {
            title: "市场代码",
            dataIndex: "marketValue",
        },
        {
            title: "账户类型",
            dataIndex: "accountTypeValue",
        },
        {
            title: "账户状态",
            dataIndex: "accountStatusValue",
        },
        {
            title: "资金账户",
            dataIndex: "assetAccount",
        },
        {
            title: "机构编码",
            dataIndex: "custOrgid",
        },
        {
            title: "分支编码",
            dataIndex: "custBranchid",
        },
        {
            title: "创建时间",
            dataIndex: "createTime",
            key: "createTime",
            width: 180,
        },
    ];
};

let getSearchFormFields = () => {
    return [
        {
            label: "用户名称",
            // label: <span>用户名称</span>,
            id: "userName",
            component: <Input placeholder="请输入" />,
        },
        {
            label: "股东账户",
            id: "accountId",
            component: <Input placeholder="请输入" />,
        },
        // {
        //     label: "合约简称",
        //     id: "contractName",
        //     component: <Input placeholder="请输入合约简称" />,
        // },
        // {
        //     label: "合约账户标识",
        //     id: "contractAccountCode",
        //     component: <Input placeholder="请输入合约账户标识" />,
        // },
    ];
};
export default class uoeSetting extends React.PureComponent {
    state = {
        searchLoading: false,
        selectRow: [],
        info: [],
        pagination: { total: 0 },
        userList: [],
    };
    //批量选择
    handleTableChange = (selectedRowKeys) => {
        console.log("批量选择");
        this.setState({
            selectRow: selectedRowKeys,
        });
    };
    getInsertFormFields = () => {
        return [
            {
                label: "用户名称",
                id: "userId",
                initialValue: "",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                    // {
                    //     message: "请输入数字",
                    //     pattern: /^[0-9]*[1-9][0-9]*$/i,
                    // },
                    {
                        validator: checkLength(20),
                        trigger: ["change", "blur"],
                    },
                ],
                component: SelectOption(this.state.userList, {
                    placeholder: "请选择",
                    allowClear: false,
                    style: {
                        width: 400,
                    },
                }),
                // component: <Input placeholder="请输入" />,
            },
            {
                label: "股东账户",
                id: "accountId",
                initialValue: "",
