import React from "react";
import CurdComponent from "@/components/CurdComponent";
// import SelectOption from "@/components/SelectOption";
// import UploadWrap from "@/components/UploadWrap";
import { Input } from "antd";

const columns = (params) => {
    return [
        {
            title: "保证金账户",
            dataIndex: "assetAccount",
            width: 150,
        },
        {
            title: "用户名称",
            dataIndex: "userName",
            width: 120,
        },
        {
            title: "余额",
            dataIndex: "balance",
            width: 170,
        },
        {
            title: "冻结资金",
            dataIndex: "frozen",
            width: 150,
        },
        {
            title: "实时保证金",
            dataIndex: "marginAmount",
            width: 150,
        },
        /**
         * 币种:1 人民币 2 港币 3 美元
         */

        /**
         * 账户类型:1股票 2期权 3融资融券 同BusinessType
         */
        {
            title: "币种",
            dataIndex: "currencyType",
            width: 150,
        },
        {
            title: "账户类型",
            dataIndex: "accountType",
            width: 150,
        },
        {
            title: "机构编码",
            dataIndex: "custOrgid",
            width: 150,
        },
        {
            title: "分支编码",
            dataIndex: "custBranchid",
            width: 150,
        },
        {
            title: "注册时间",
            dataIndex: "createTime",
            width: 200,
        },
        {
            title: "更新时间",
            dataIndex: "updateTime",
            width: 200,
        },
        // {
        //     title: "资金版本号",
        //     dataIndex: "vers",
        //     width: 100,
        // },
        {
            title: "版本号",
            dataIndex: "version",
            width: 120,
        },
    ];
};
let getSearchFormFields = () => {
    return [
        // {
        //     label: "保证金账户",
        //     id: "assetAccount",
        //     component: <Input placeholder="请输入保证金账户" />,
        // },
        {
            label: "用户名称",
            // label: <span>用&nbsp;&nbsp;户&nbsp;ID</span>,
            // id: "userId",
            id: "userName",
            component: <Input placeholder="请输入" />,
        },
    ];
};
const getInsertFormFields = () => {
    return [
        {
            label: "保证金账户",
            id: "assetAccount",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(32),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "用户名称",
            id: "userName",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(30),
                    trigger: ["change", "blur"],
                },
            ],
            component: (
                // <Input placeholder="请输入" readOnly disabled />
                <Input placeholder="请输入" />
            ),
        },
        {
            label: "余额",
            id: "balance",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(20),
                    trigger: ["change", "blur"],
                },
            ],
            component: (
                // <Input placeholder="请输入" readOnly disabled />
                <Input placeholder="请输入" />
            ),
        },
        {
            label: "冻结资金",
            id: "frozen",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(20),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "实时保证金",
            id: "marginAmount",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(20),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
    ];
};
const getUpdateFormFields = () => {
    return [
        {
            label: "保证金账户",
            id: "assetAccount",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(32),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "用户名称",
            id: "userName",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(30),
                    trigger: ["change", "blur"],
                },
            ],
            component: (
                // <Input placeholder="请输入" readOnly disabled />
                <Input placeholder="请输入" />
            ),
        },
        {
            label: "余额",
            id: "balance",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(20),
                    trigger: ["change", "blur"],
                },
            ],
            component: (
                // <Input placeholder="请输入" readOnly disabled />
                <Input placeholder="请输入" />
            ),
        },
        {
            label: "冻结资金",
            id: "frozen",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(20),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "实时保证金",
            id: "marginAmount",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(20),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "注册时间",
            id: "createTime",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
            ],
            component: (
                // <Input placeholder="请输入" readOnly disabled />
                <Input placeholder="请输入" readOnly disabled />
            ),
        },
        {
            label: "更新时间",
            id: "updateTime",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
            ],
            component: (
                // <Input placeholder="请输入" readOnly disabled />
                <Input placeholder="请输入" readOnly disabled />
            ),
        },
        {
            label: "版本号",
            id: "version",
            initialValue: "",
            rules: [
                {
