import React from "react";
import CurdComponent from "@/components/CurdComponent";
import SelectOption from "@/components/SelectOption";
// import UploadWrap from "@/components/UploadWrap";

import { Input, Modal, Switch, Form, message, Tooltip, Icon } from "antd";
import styles from "./style.module.less";
const getInsertFormFields = () => {
    return [
        {
            label: "用户ID",
            id: "UserId",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "用户密码",
            id: "UserPasswd",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "用户名",
            id: "UserName",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "用户类型",
            id: "UserType",
            initialValue: "1",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
            ],
            component: SelectOption(dict.userType, {
                placeholder: "请选择",
                // allowClear: true,
                style: {
                    width: 400,
                },
            }),
        },
        {
            label: "父级用户Id",
            id: "UuserId",
            initialValue: "0",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "用户风控组",
            id: "RiskGroup",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
            ],
            component: (
                // <Input placeholder="请输入" readOnly disabled />
