import React from "react";
import styles from "./style.module.less";
import echarts from "echarts";
import {
    SearchForm,
    Input,
    TimePicker,
    Form,
    Button,
    Icon,
    DatePicker,
    Select,
} from "antd";

const { RangePicker } = DatePicker;
const { Option } = Select;
import moment from "moment";
import { lte } from "semver";
class Ram2 extends React.PureComponent {
    state = {
        echatObj: {},
    };
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                let noTime = values["pickerTime"].length < 1;
                let params = {
                    securityId: values.securityId,
                    algorithmId: values.algorithmId,
                    uuserId: values.uuserId,
                    startTime: noTime
                        ? ""
                        : Date.parse(values["pickerTime"][0]),
                    endTime: noTime ? "" : Date.parse(values["pickerTime"][1]),
                };
                this.getData(params);
            }
        });
    };
    getData = (params, count = 0) => {
        http.post({
            url: "/statistics/algo",
            data: params,
        }).then((res) => {
            if (res.code == 0) {
                this.setState({
                    echatObj: res.data,
                });
                console.log(this.state.echatObj, 9999);
                let data = {
                    algoOrder: [
                        { x: "1970/01/01 08:00", y: 1 },
                        { x: "2022/06/21 16:46", y: 4 },
                    ],
                    cancelAlgoOrder: [],
                    childOrder: [],
                    dealAlgoOrder: [],
                };
                let echartLen = Object.keys(data);
                echartLen.forEach((item) => {
                    console.log(item);
                    this.generateChart(item);
                });
            } else {
                message.error("服务异常");
            }
        });
    };
    generateChart = (list, name) => {
        let option = {
            textStyle: {
                color: "#333",
            },
            tooltip: {
                trigger: "axis",
                backgroundColor: "#1F2329",
                boxShadow: "0px 2px 8px 0px rgba(0, 0, 0, 0.15)",
                borderColor: "#1F2329",
                textStyle: {
                    color: "#fff",
                },
            },
            dataset: {
                dimensions: ["x", "y"],
                source: res.data,
            },
            grid: {
                left: "25px",
                right: "5px",
                bottom: "9%",
                top: "33px",
                containLabel: true,
            },
            xAxis: {
                type: "category",
                boundaryGap: false,
                splitLine: {
                    show: true,
                    lineStyle: {
                        color: "#E9E9E9",
                        type: "dashed",
                    },
                },
                axisLabel: {
                    // interval: 0,
                    // rotate: 30,
                },
                axisTick: {
                    show: true, //显示X轴刻度
                    lineStyle: {
                        color: "#E9E9E9",
                    },
                },
                axisLine: {
                    // 刻度线的颜色
                    show: false,
                },
                axisPointer: {
                    type: "line",
                    lineStyle: { color: "#BDBEBF" },
                },
            },
            yAxis: [
                {
                    type: "value",
                    name: "单位：（个）",
                    nameLocation: "end",
                    axisLine: {
                        show: false,
                    },
                    nameTextStyle: {
                        color: "#666",
                    },
                    axisTick: {
                        show: false, //隐藏X轴刻度
                    },
                    splitLine: {
                        show: true,
                        lineStyle: {
                            color: "#E9E9E9",
                            type: "dashed",
                        },
                    },
                    nameTextStyle: {
                        padding: [0, 0, 0, 40],
                    },
                },
            ],
            series: [
                {
                    name: "在线人数",
                    type: "line",
                    smooth: true,
                    showSymbol: false,
                    // symbol: "circle",
                    itemStyle: {
                        normal: {
                            color: "#65A6FF",
                        },
                    },
                    areaStyle: {
                        normal: {
                            color: new echarts.graphic.LinearGradient(
                                0,
                                0,
                                0,
                                1,
                                [
                                    {
                                        offset: 0,
                                        color: "rgba(50, 129, 255, 0.2)",
                                    },
                                    {
                                        offset: 1,
                                        color: "rgba(255,255,255,0)",
                                    },
                                ],
                                false
                            ),
                            shadowColor: "rgba(0, 0, 0, 0.1)",
                            shadowBlur: 10,
                        },
                    },
                },
            ],
        };
        // echarts.init(document.getElementById(item).setOption(item+"Option")
        var myChart = echarts.init(document.getElementById(item));
        myChart.setOption(item + "Option");
        myChart.resize();
    };
    componentDidMount() {
        this.getData({
            securityId: "",
            algorithmId: "",
            uuserId: "",
            startTime: "",
            endTime: "",
        });
    }
    render() {
        const { getFieldDecorator } = this.props.form;
        return (
            <div className={styles.container}>
                <div className={styles.search}>
                    <Form layout="inline" onSubmit={this.handleSubmit}>
                        <Form.Item>
                            {getFieldDecorator("hostId", {
                                initialValue: "1",
                            })(
                                <Select
                                    showSearch
                                    style={{ width: 160 }}
                                    placeholder="选择单位"
                                    optionFilterProp="number"
                                    filterOption={(input, option) =>
                                        option.props.children
                                            .toLowerCase()
                                            .indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    <Option value="1">数量</Option>
                                    <Option value="2">笔数</Option>
                                    <Option value="3">金额</Option>
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item style={{ marginLeft: "12px" }}>
                            {getFieldDecorator("algorithmId", {
                                initialValue: "",
                            })(
                                <Select
                                    showSearch
                                    style={{ width: 160 }}
                                    placeholder="选择股票"
                                    optionFilterProp="children"
                                    filterOption={(input, option) =>
                                        option.props.children
                                            .toLowerCase()
                                            .indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    <Option value="">全部股票</Option>
                                    <Option value="1">80</Option>
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item style={{ marginLeft: "12px" }}>
                            {getFieldDecorator("length", {
                                initialValue: "",
                            })(
                                <Select
                                    showSearch
                                    style={{ width: 160 }}
                                    placeholder="选择算法"
                                    optionFilterProp="children"
                                    filterOption={(input, option) =>
                                        option.props.children
                                            .toLowerCase()
                                            .indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    <Option value="">全部算法</Option>
                                    <Option value="1">日内回转</Option>
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item style={{ marginLeft: "12px" }}>
                            {getFieldDecorator("uuserId", {
                                initialValue: "",
                            })(
                                <Select
                                    showSearch
                                    style={{ width: 160 }}
                                    placeholder="选择用户"
                                    optionFilterProp="children"
                                    filterOption={(input, option) =>
                                        option.props.children
                                            .toLowerCase()
                                            .indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    <Option value="">全部用户</Option>
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item style={{ marginLeft: "12px" }}>
                            {getFieldDecorator("pickerTime")(
                                <RangePicker
                                    style={{ width: 432 }}
                                    showTime
                                    format="YYYY-MM-DD HH:mm:ss"
                                />
                            )}
                        </Form.Item>
                        <Form.Item style={{ float: "right" }}>
                            <Button
                                type="primary"
                                htmlType="submit"
                                style={{ width: 76 }}
                            >
                                确定
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
                <div className={styles.chart}>
                    <div id="algoOrder" className={styles.chartBox}></div>
                    <div id="childOrder" className={styles.chartBox}></div>
                    <div id="cancelAlgoOrder" className={styles.chartBox}></div>
                    <div id="dealAlgoOrder" className={styles.chartBox}></div>
                    {/* <div
                        id="main_1"
                        style={{ width: "48%", height: "500px", float: "left" }}
                    ></div>
                    <div
                        id="main_2"
                        style={{
                            width: "48%",
                            height: "500px",
                            float: "right",
                        }}
                    ></div> */}
                </div>
            </div>
        );
    }
}
export default Form.create()(Ram2);
