import React from "react";
import CurdComponent from "@/components/CurdComponent";
import SelectOption from "@/components/SelectOption";
// import UploadWrap from "@/components/UploadWrap";

import {
    Input,
    Modal,
    Switch,
    Form,
    message,
    Tooltip,
    Icon,
    AutoComplete,
} from "antd";
import styles from "./style.module.less";
class userInfo extends React.PureComponent {
    state = {
        searchLoading: false,
        selectRow: [],
        info: [],
        updateModalVisible: false,
        updateModalVisible2: false,
        riskGroup: [],
        userRiskConfig: {},
        pagination: { total: 0 },
        riskList: [],
        algoGroupList: [],
    };
    getInsertFormFields = () => {
        return [
            {
                label: "用户ID",
                id: "UserId",
                initialValue: "",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "用户密码",
                id: "UserPasswd",
                initialValue: "",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "用户名",
                id: "UserName",
                initialValue: "",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "用户类型",
                id: "UserType",
                initialValue: "1",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: SelectOption(dict.userType, {
                    placeholder: "请选择",
                    // allowClear: true,
                    style: {
                        width: 400,
                    },
                }),
            },
            {
                label: "父级用户ID",
                id: "UuserId",
                initialValue: "0",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "用户风控组",
                id: "RiskGroup",
                initialValue: "",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component:
                    // <Input placeholder="请输入" readOnly disabled />
                    SelectOption(this.state.riskList, {
                        placeholder: "请选择用户风控组",
                    }),
            },
            {
                label: "算法权限组",
                id: "AlgoGroup",
                initialValue: "",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component:
                    // <Input placeholder="请输入" readOnly disabled />
                    SelectOption(this.state.riskList, {
                        placeholder: "请选择算法权限组",
                    }),
            },
            // {
            //     label: "算法属性",
            //     id: "AlgoProperty",
            //     initialValue: "",
            //     rules: [
            //         // {
            //         //     required: true,
            //         //     message: "参数不能为空",
            //         // },
            //     ],
            //     component: <Input placeholder="请输入" />,
            // },
        ];
    };
    getUpdateFormFields = () => {
        return [
            {
                label: "用户ID",
                id: "UserId",
                initialValue: "",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "用户密码",
                id: "UserPasswd",
                initialValue: "",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "用户名",
                id: "UserName",
                initialValue: "",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "用户类型",
                id: "UserType",
                initialValue: "1",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: SelectOption(dict.userType, {
                    placeholder: "请选择",
                    // allowClear: true,
                    style: {
                        width: 400,
                    },
                }),
            },
            {
                label: "父级用户ID",
                id: "UuserId",
                initialValue: "0",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
            {
                label: "用户风控组",
                id: "RiskGroup",
                initialValue: "",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: (
                    // <Input placeholder="请输入" readOnly disabled />
                    <Input placeholder="请输入" />
                ),
            },
            {
                label: "算法权限组",
                id: "AlgoGroup",
                initialValue: "",
                rules: [
                    {
                        required: true,
                        message: "参数不能为空",
                    },
                ],
                component: <Input placeholder="请输入" />,
            },
        ];
    };
    getSearchFormFields = () => {
        return [
            {
                label: "用户ID",
                id: "userId",
                component: <Input placeholder="请输入" />,
            },
            {
                label: "用户类型",
                id: "userType",
                // initialValue: "1",
                component: SelectOption(dict.userType, {
                    placeholder: "请选择",
                    allowClear: true,
                    style: {
                        width: 183,
                    },
                }),
            },
            {
                label: "用户风控组",
                id: "riskGroup",
                component: <Input placeholder="请输入" />,
            },
            {
                label: "算法权限组",
                id: "algoGroup",
                component: <Input placeholder="请输入" />,
            },
            {
                label: "父级用户名",
                id: "fatherId",
                component: <Input placeholder="请输入" />,
            },
        ];
    };
    //批量选择
    handleTableChange = (selectedRowKeys) => {
        console.log("批量选择");
        this.setState({
            selectRow: selectedRowKeys,
        });
    };

    handleInsertRecord = (params) => {
        // params = {
        //     UserId: "atest0000003",
        //     UserName: "test3",
        //     UserPasswd: "test_0000003",
        //     UserType: 1,
        //     // RiskGroup: 1,
        //     // AlgoGroup: 1,
        //     // AlgoProperty: "20",
        //     UuserId: 1,
        // };
        params.UserType = params.UserType / 1;
        params.RiskGroup = params.RiskGroup / 1;
        params.AlgoGroup = params.AlgoGroup / 1;
        params.UuserId = params.UuserId / 1;
        console.log("新增接口", params);
        http.post({
            url: "/user/addUserInfo",
            data: params,
        }).then((res) => {
            console.log(res);
            let msg = res.message;
            if (res.code == 0) {
                message.success(msg);
                // this.getData();
            } else if (res.code == 20000) {
                message.error(
                    msg.substring(msg.indexOf("[") + 1, msg.indexOf("]"))
                );
            } else {
                message.error(msg);
            }
        });
    };
    //更新记录
    handleUpdateRecord = ({ form }) => {
        // console.log(form.getFieldsValue());
        // return;
        let params = form.getFieldsValue();
        params.id = this.record.id / 1;
        params.UserType = params.UserType / 1;
        params.RiskGroup = params.RiskGroup / 1;
        params.AlgoGroup = params.AlgoGroup / 1;
        params.UuserId = params.UuserId / 1;
        //发送更新请求
        http.post({
            url: "/user/updateUserInfo",
            data: params,
        }).then((res) => {
            console.log(res);
            let msg = res.message;
            if (res.code == 0) {
                message.success(msg);
                // this.getData();
            } else if (res.code == 20000) {
                message.error(
                    msg.substring(msg.indexOf("[") + 1, msg.indexOf("]"))
                );
            } else {
                message.error(msg);
            }
            // this.isAction = true;
        });
    };
    //填入更新数据
    setUpdateModal = ({ form, record }) => {
        // console.log(record, form);
        this.record = record;
        form.setFieldsValue({
            UserId: record.userId,
            UserName: record.userName,
            UserPasswd: record.userPasswd,
            UserType: record.userType + "",
            RiskGroup: record.riskGroup,
            AlgoGroup: record.algoGroup,
            UuserId: record.uuserId,
            // UserType: record.UserType,
        });
    };
    columns = (params) => {
        return [
            {
                title: "ID",
                dataIndex: "id",
                width: 80,
            },
            {
                title: "用户ID",
                dataIndex: "userId",
                width: 150,
            },
            {
                title: "用户名",
                dataIndex: "userName",
                key: "userName",
            },
            {
                title: "用户类型",
                dataIndex: "userTypeValue",
                // key: "userType",
            },
            {
                title: "用户风控组",
                dataIndex: "riskGroup",
                // width: 120,
                render: (text, record) => (
                    <div
                        onClick={(e) => {
                            this.handleUpdate(record);
                        }}
                    >
                        <Tooltip title="修改风控组">
                            {record.riskGroup}
                            <Icon type="edit" style={{ color: "#1899ff" }} />
                        </Tooltip>
                    </div>
                ),
            },
            {
                title: "算法权限组",
                dataIndex: "algoGroup",
                // width: 120,
                render: (text, record) => (
                    <div
                        onClick={(e) => {
                            this.handleUpdateAlgo(record);
                        }}
                    >
                        <Tooltip title="修改算法权限组">
                            {record.algoGroup}
                            <Icon type="edit" style={{ color: "#1899ff" }} />
                        </Tooltip>
                    </div>
                ),
            },
            // {
            //     title: "业务类型",
            //     dataIndex: "businessType",
            // },
            // {
            //     title: "登录状态",
            //     dataIndex: "loginStatus",
            // },
            {
                title: "父级用户ID",
                dataIndex: "uuserId",
            },
            {
                title: "父级用户名",
                dataIndex: "fatherId",
            },
            {
                title: "注册时间",
                dataIndex: "createTime",
                key: "createTime",
                width: 180,
            },
            // {
            //     title: "操作",
            //     key: "operation",
            //     fixed: "right",
            //     width: 100,
            //     render: (text, record) => (
            //         <a
            //             onClick={(e) => {
            //                 this.handleUpdate(record);
            //             }}
            //         >
            //             编辑
            //         </a>
            //     ),
            // },
        ];
    };
    //填入更新记录
    handleUpdate = (record) => {
        console.log("更新记录", record);
        this.record = record;
        this.setState(
            {
                updateModalVisible: true,
                riskGroup: [],
            },
            () => {
                this.getRiskGroup(record.riskGroup);
                this.props.form.setFieldsValue({
                    riskGroup: record.riskGroup + "",
                    userName: record.userName,
                });
            }
        );
    };
    //修改算法权限组
    handleUpdateAlgo = (record) => {
        console.log("修改算法权限组", record);
        this.record = record;
        this.setState(
            {
                updateModalVisible: true,
                riskGroup: [],
            },
            () => {
                this.getRiskGroup(record.riskGroup);
                this.props.form.setFieldsValue({
                    riskGroup: record.riskGroup + "",
                    userName: record.userName,
                });
            }
        );
    };

    handleUpdateModalOk = () => {
        let data = this.props.form.getFieldsValue();
        data.userId = this.record.id;
        console.log(data);
        this.updateUserRiskGroup(data);

        this.setState({
            updateModalVisible: false,
        });
    };
    handleUpdateModalCancel = () => {
        this.setState({
            updateModalVisible: false,
        });
    };
    //删除记录
    handleDeleteRecord = (record) => {
        console.log("删除记录 ", record);
    };
    inputChange = (e) => {
        // console.log(e);
        // let val = e.target.value;
        let val = e;
        if (this.inputTimeout) {
            clearTimeout(this.inputTimeout);
        }
        this.inputTimeout = setTimeout(() => {
            this.getRiskGroup(val);
        }, 1000);
    };
    //获取所有风控组
    getAllRiskGroup = (params = {}) => {
        // return;
        http.post({
            // url: "/risk/queryRisk",
            url: "/risk/riskList",
            data: params,
        }).then((res) => {
            // console.log(res);
            let idArr = [];
            if (res.data && res.data.length > 0) {
                let dataArr = res.data;
                if (dataArr.length > 0) {
                    idArr = dataArr.map((item) => {
                        let obj = {};
                        obj.key = item.id;
                        obj.value = item.id;
                        return obj;
                    });
                    // console.log(idArr);
                }
            }
            this.setState({
                riskList: idArr,
            });
        });
    };
    //获取所有算法权限组
    getAllRiskGroup = (params = {}) => {
        // return;
        http.post({
            // url: "/risk/queryRisk",
            url: "/algo-group-info/list",
            data: params,
        }).then((res) => {
            // console.log(res);
            let idArr = [];
            if (res.data && res.data.length > 0) {
                let dataArr = res.data;
                if (dataArr.length > 0) {
                    idArr = dataArr.map((item) => {
                        let obj = {};
                        obj.key = item.id;
                        obj.value = item.id;
                        return obj;
                    });
                    // console.log(idArr);
                }
            }
            this.setState({
                algoGroupList: idArr,
            });
        });
    };
    //修改用户风控组
    updateUserRiskGroup = (data) => {
        let params = {
            userId: data.userId,
            riskGroup: data.riskGroup / 1,
        };
        console.log(params);
        // return;
        http.post({
            url: "/risk/modifyUserRiskGroup",
            data: params,
        }).then((res) => {
            // console.log(res);
            if (res.code == 0) {
                message.success(res.message);
                // showTip(this, res.message);
                // this.isAction = true;
                this.getData(this.searchParam, this.state.pagination);
                // this.getData();
            } else {
                message.error(res.message);
                this.isAction = true;
            }
        });
    };
    //查询用户风控配置
    getRiskGroup = (id) => {
        let params = {
            id: id / 1,
        };
        // console.log(params);
        http.post({
            url: "/risk/queryRiskConfig",
            data: params,
        }).then((res) => {
            console.log(res);
            if (res.code != 0) {
                message.error("查询风控配置组失败！");
                return;
            }
            let config = JSON.parse(res.data);
            // this.setState({ userRiskConfig: config });
            // console.log(config);
            let enable = (config.RiskEnable / 1).toString(2).padStart(9, "0");
            // let enableArr = enable.split("");
            let enableArr = enable.split("").map((item) => item == 1);
            // console.log(enableArr);
            this.props.form.setFieldsValue({
                // riskGroup: record.riskGroup + "",
                byte0: enableArr[8],
                byte1: enableArr[7],
                byte2: enableArr[6],
                byte3: enableArr[5],
                byte4: enableArr[4],
                byte5: enableArr[3],
                byte6: enableArr[2],
                byte7: enableArr[1],
                byte8: enableArr[0],
                // riskName: config.RiskName,
                entrustItemThreshold: config.EntrustItemThreshold,
                entrustItemLimit: config.EntrustItemLimit,
                entrustSeconds: config.EntrustSeconds,
                entrustTotalThreshold: config.EntrustTotalThreshold,
                cancelEntrustItemThreshold: config.CancelEntrustItemThreshold,
                cancelRatioLimit: config.CancelRatioLimit,
                failedEntrustItemThreshold: config.FailedEntrustItemThreshold,
                failedRatioLimit: config.FailedRatioLimit,
                entrustExecEntrustItemThreshold:
                    config.EntrustExecEntrustItemThreshold,
                entrustExecRatioLimit: config.EntrustExecRatioLimit,
                netBuyEntrustItemThreshold: config.NetBuyEntrustItemThreshold,
                netBuyAmountLimit: config.NetBuyAmountLimit,
                cancelItemLimit: config.CancelItemLimit,
                cancelSeconds: config.CancelSeconds,
                cancelGapSeconds: config.CancelGapSeconds,
                tradeItemLimit: config.TradeItemLimit,
                tradeSeconds: config.TradeSeconds,
                tradeQtyLimit: config.TradeQtyLimit,
                tradeAmountLimit: config.TradeAmountLimit,
            });
        });
    };
    getData = (params = {}, pagination = { current: 1, pageSize: 10 }) => {
        params = {
            ...params,
            pageId: pagination.current,
            pageNum: pagination.pageSize,
        };
        http.post({
            // url: "/option/assetInfo/selectList",
            url: "/user/selectByCondition",
            data: params,
        }).then((res) => {
            console.log(res);
            //解析数据字典
            if (res.data.records && res.data.records.length > 0) {
                parseDictValue(res.data.records);
                // showTip(this);
            } else {
                message.info("查询结果为空");
            }
            let pgn = {
                current: res.data.current,
                pageSize: pagination.pageSize,
                total: res.data.total || 0,
            };
            this.setState({
                info: res.data.records,
                pagination: pgn,
            });
        });
    };
    handleSearch = (params, pagination) => {
        this.searchParam = params;
        this.getData(params, pagination);
    };
    handleDownload = () => {
        window.location.href = window.baseURL + "/user/download";
    };
    componentDidMount() {
        this.getData();
        this.getAllRiskGroup();
    }
    render() {
        let scroll = { x: 1000, y: 445 };
        let info = this.state.info;
        //批量
        // let { selectRow } = this.state;
        // const rowSelection = {
        //     selectRow,
        //     onChange: this.handleTableChange,
        // };
        let { getFieldDecorator } = this.props.form;
        let labelCol = {
            xs: 12,
        };
        let wrapperCol = {
            xs: 24 - labelCol.xs,
        };
        // let formItemLayout = {
        //     labelCol,
        //     wrapperCol,
        // };
        return (
            <div className={styles.userInfo}>
                <CurdComponent
                    pageSize={10}
                    // rowKey={"index"}
                    // isShowSearchForm={false}
                    // btnText2="查全部"
                    onSearchClick={this.handleSearch}
                    getSearchFormFields={this.getSearchFormFields}
                    // searchLoading={this.state.searchLoading}
                    insertBtnText={"新增"} // 不传 就没新增按钮
                    getInsertFormFields={this.getInsertFormFields}
                    insertRecord={this.handleInsertRecord}
                    // col="2"
                    width="600px"
                    pagination={this.state.pagination}
                    getUpdateFormFields={this.getUpdateFormFields}
                    setUpdateModal={this.setUpdateModal}
                    updateRecord={this.handleUpdateRecord} // 不传 就没编辑
                    // deleteRecord={this.handleDeleteRecord} // 不传 就没删除
                    centered={true}
                    columns={this.columns}
                    dataSource={info}
                    scroll={scroll}
                    // rowSelection={rowSelection} //批量选择 操作
                >
                    <div
                        urlPrefix="/user"
