                                            },
                                        ],
                                        initialValue: "0",
                                    })(
                                        <Input
                                            placeholder="请输入"
                                            suffix="秒"
                                            disabled={true}
                                        />
                                    )}
                                </Form.Item>
                            </div>
                            <div
                                className={
                                    styles.rowFlex + " " + styles.marLose14
                                }
                            >
                                <Form.Item
                                    label={
                                        <label title="下单总量笔数阈值">
                                            下单总量
                                        </label>
                                    }
                                    // {...formItemLayout}
                                >
                                    {getFieldDecorator("tradeQtyLimit", {
                                        rules: [
                                            {
                                                required: true,
                                                message: "请输入",
                                            },
                                            {
                                                message: "请输入正整数",
                                                pattern: /^\d+$/i,
                                            },
                                        ],
                                        initialValue: "0",
                                    })(
                                        <Input
                                            placeholder="请输入"
                                            suffix="股"
                                            disabled={true}
                                        />
                                    )}
                                </Form.Item>
                                <div style={{ width: 60 }}></div>
                                <Form.Item
                                    label={
                                        <label title="下单总金额阈值">
                                            下单总金额
                                        </label>
                                    }
                                    // {...formItemLayout}
                                >
                                    {getFieldDecorator("tradeAmountLimit", {
                                        rules: [
                                            {
                                                required: true,
                                                message: "请输入",
                                            },
                                            {
                                                message: "请输入正整数",
                                                pattern: /^\d+$/i,
                                            },
                                        ],
                                        initialValue: "0",
                                    })(
                                        <Input
                                            placeholder="请输入"
                                            suffix="元"
                                            disabled={true}
                                        />
                                    )}
                                </Form.Item>
                            </div>
                        </div>
                    </Form>
                </Modal>
                <Modal
                    title={"修改算法权限组"}
                    visible={this.state.updateModalVisible2}
                    onOk={this.handleUpdateModalOk}
                    onCancel={this.handleUpdateModalCancel}
                    width={788}
                    centered
                >
                    <Form layout={"vertical"}>
                        <div>
                            <div className={styles.tit}>
                                <div className={styles.text}>风控组</div>
                            </div>
                            <div
                                className={styles.rowFlex}
                                style={{
                                    position: "relative",
                                }}
                                id="area"
                            >
                                <Form.Item label="用户名">
                                    {getFieldDecorator("userName")(
                                        <Input placeholder="请输入" disabled />
                                    )}
                                </Form.Item>
                                <div style={{ width: 60 }}></div>
                                <Form.Item
                                    // className={styles.marLose14}
                                    label={
                                        <label title="请输入已配置成功的风控组,否则风控组不能修改成功!">
                                            修改风控组
                                        </label>
                                    }
                                >
                                    {getFieldDecorator("riskGroup", {
                                        rules: [
                                            {
                                                required: true,
                                                message: "请输入",
                                            },
                                        ],
                                    })(
                                        // <AutoComplete
                                        //     style={{ width: 200 }}
                                        //     dataSource={this.state.riskList}
                                        //     placeholder="请输入风控组ID"
                                        //     getPopupContainer={() =>
                                        //         document.getElementById("area")
                                        //     }
                                        //     onChange={this.inputChange}
                                        //     filterOption={(
                                        //         inputValue,
                                        //         option
                                        //     ) =>
                                        //         option.props.children
                                        //             .toUpperCase()
                                        //             .indexOf(
                                        //                 inputValue.toUpperCase()
                                        //             ) !== -1
                                        //     }
                                        // />
                                        SelectOption(this.state.riskList, {
                                            placeholder: "请选择",
                                            onChange: this.inputChange,
                                        })
                                        // <Input
                                        //     placeholder=""
                                        //     onChange={this.inputChange}
                                        // />
                                    )}
                                </Form.Item>
                            </div>
                        </div>
                    </Form>
                </Modal>
            </div>
        );
    }
}

export default Form.create()(userInfo);
