import React from "react";
import styles from "./style.module.less";
import echarts from "echarts";
import moment from "moment";
import { connect } from "react-redux";
import {
    SearchForm,
    Input,
    TimePicker,
    Form,
    Button,
    Icon,
    DatePicker,
    Select,
} from "antd";

const { RangePicker } = DatePicker;
const { Option } = Select;
class Cpu extends React.PureComponent {
    state = {};
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            let noTime = values["pickerTime"].length < 1;
            let params = {
                hostId: values.hostId,
                startTime: noTime
                    ? ""
                    : moment(values["pickerTime"][0]).format(
                          "YYYY/MM/DD HH:mm:ss"
                      ),
                endTime: noTime
                    ? ""
                    : moment(values["pickerTime"][1]).format(
                          "YYYY/MM/DD HH:mm:ss"
                      ),
            };
            this.getData(params);
        });
    };
    getData = (params) => {
        http.post({
            data: params,
            url: "/ssh/cpu",
        }).then((res) => {
            if (res.code == 0) {
                let list = res.data;
                let seriesList = list.series;
                if (seriesList.length == 0) {
                    message.error("该时间段暂无数据");
                } else {
                    seriesList.forEach((item) => {
                        item.data = item.y;
                        item.smooth = true;
                        item.showSymbol = false;
                        item.itemStyle = {
                            normal: {
                                color: "#65A6FF",
                            },
                        };
                    });
                    let option = {
                        textStyle: {
                            color: "#333",
                        },
                        tooltip: {
                            show: false,
                            trigger: "axis",
                            backgroundColor: "#1F2329",
                            boxShadow: "0px 2px 8px 0px rgba(0, 0, 0, 0.15)",
                            borderColor: "#1F2329",
                            textStyle: {
                                color: "#fff",
                            },
                        },
                        legend: {
                            // data: ["CPU1", "CPU2", "CPU3"],
                            left: 0,
                        },
                        grid: {
                            left: "1%",
                            right: "0%",
                            bottom: "9%",
                            top: "60px",
                            containLabel: true,
                        },
                        xAxis: {
                            type: "category",
                            boundaryGap: false,
                            splitLine: {
                                show: true,
                                lineStyle: {
                                    color: "#E9E9E9",
                                    type: "dashed",
                                },
                            },
                            axisTick: {
                                show: true, //显示X轴刻度
                                lineStyle: {
                                    color: "#E9E9E9",
                                },
                            },
                            axisLine: {
                                // 刻度线的颜色
                                show: false,
                            },
                            axisPointer: {
                                type: "line",
                                lineStyle: { color: "#BDBEBF" },
                            },
                            data: list.x,
                        },
                        yAxis: [
                            {
                                type: "value",
                                name: "单位（%）",
                                nameLocation: "end",
                                axisLine: {
                                    show: false,
                                },
                                nameTextStyle: {
                                    color: "#666",
                                },
                                axisTick: {
                                    show: false, //隐藏X轴刻度
                                },
                                splitLine: {
                                    show: true,
                                    lineStyle: {
                                        color: "#E9E9E9",
                                        type: "dashed",
                                    },
                                },
                                axisLabel: {
                                    formatter: "{value} GB",
                                },
                                nameTextStyle: {
                                    padding: [0, 43, 0, 0],
                                },
                                axisLabel: {
                                    formatter: "{value}%",
                                },
                            },
                        ],
                        series: list.series,
                        dataZoom: [
                            {
                                type: "inside",
                            },
                            {
                                type: "slider",
                                height: "20px",
                            },
                        ],
                    };
                    var dom1 = document.getElementById("main1");
                    var myChart = echarts.init(dom1);
                    myChart.resize();
                    myChart.setOption(option);
                }
            } else {
                message.error("服务异常");
            }
        });
    };

    chartResize = () => {
        console.log(this);
        console.time("echarts");
        var dom1 = document.getElementById("main1");
        echarts.init(dom1).resize();
        console.timeEnd("echarts");
    };
    componentDidMount() {
        this.getData({
            hostId: "1",
            startTime: "",
            endTime: "",
        });
        // window.addEventListener("resize", () => {
        //     this.chartResize();
        // });
    }
    render() {
        console.log(this.props.path);
        window.cpuResize = this.chartResize;
        if (this.props.path == "/main/chart/cpu") {
            console.log("设置cpu resize 事件");
            window.addEventListener("resize", window.cpuResize);
        } else {
            console.log("取消cpu resize 事件!");
            // console.log(window);
            // window.removeEventListener("resize", chartResize, true);
            window.removeEventListener("resize", window.cpuResize);
            console.log(222);
        }
        const { getFieldDecorator } = this.props.form;
        return (
            <div className={styles.container}>
                <div className={styles.search}>
                    <Form layout="inline" onSubmit={this.handleSubmit}>
                        <Form.Item>
                            {getFieldDecorator("hostId", {
                                initialValue: "1",
                            })(
                                <Select
                                    showSearch
                                    style={{ width: 160 }}
                                    placeholder="选择CPU"
                                    optionFilterProp="children"
                                    filterOption={(input, option) =>
                                        option.props.children
                                            .toLowerCase()
                                            .indexOf(input.toLowerCase()) >= 0
                                    }
                                >
                                    <Option value="">全部</Option>
                                    <Option value="1">80</Option>
                                </Select>
                            )}
                        </Form.Item>
                        <Form.Item style={{ marginLeft: "12px" }}>
                            {getFieldDecorator("pickerTime", {
                                initialValue: [],
                            })(
                                <RangePicker
                                    style={{ width: 432 }}
                                    showTime
                                    format="YYYY-MM-DD HH:mm:ss"
                                />
                            )}
                        </Form.Item>
                        <Form.Item style={{ float: "right" }}>
                            <Button
                                type="primary"
                                htmlType="submit"
                                style={{ width: 76 }}
                            >
                                确定
                            </Button>
                        </Form.Item>
                    </Form>
                </div>
                <div>
                    <div id="main1" className={styles.chart}></div>
                </div>
            </div>
        );
    }
}
const mapStateToProps = (state, ownProps) => {
    return {
        path: state.RouterModel.path,
    };
};
export default connect(mapStateToProps, null)(Form.create()(Cpu));
// export default Form.create()(Cpu);
