import React from "react";
import CurdComponent from "@/components/CurdComponent";
import SelectOption from "@/components/SelectOption";
import { Input, DatePicker } from "antd";

const { RangePicker } = DatePicker;
const columns = (params) => {
    return [
        {
            title: "序号",
            dataIndex: "id",
            key: "id",
        },
        {
            title: "用户名称",
            dataIndex: "operateId",
            key: "operateId",
        },
        {
            title: "操作模块",
            dataIndex: "operateModule",
        },
        {
            title: "操作项目",
            dataIndex: "operateItem",
        },
        {
            title: "操作内容",
            dataIndex: "operateContent",
        },
        {
            title: "操作参数",
            dataIndex: "operateParams",
        },
        {
            title: "时间",
            dataIndex: "createTime",
            key: "createTime",
            width: 180,
        },
    ];
};
const getInsertFormFields = () => {
    return [
        {
            label: "用户名称",
            id: "userName",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                // {
                //     message: "请输入数字",
                //     pattern: /^[0-9]*[1-9][0-9]*$/i,
                // },
                {
                    validator: checkLength(20),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "股东账户",
            id: "accountId",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(12),
                    trigger: ["change", "blur"],
                },
            ],
            component: (
                // <Input placeholder="请输入" readOnly disabled />
                <Input placeholder="请输入" />
            ),
        },
        {
            label: "市场代码",
            id: "market",
            initialValue: "1",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
            ],
            component: SelectOption(dict.market, {
                placeholder: "请选择",
                allowClear: false,
                style: {
                    width: 400,
                },
            }),
        },
        {
            label: "账户类型",
            id: "accountType",
            initialValue: "1",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
            ],
            component: SelectOption(dict.accountType, {
                placeholder: "请选择",
                allowClear: false,
                style: {
                    width: 400,
                },
            }),
        },
        {
            label: "账户状态",
            id: "accountStatus",
            initialValue: "1",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
            ],
            component: SelectOption(dict.accountStatus, {
                placeholder: "请选择",
                // allowClear: true,
                style: {
                    width: 400,
                },
            }),
        },
        {
            label: "资金账户",
            id: "assetAccount",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(25),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "机构编码",
            id: "custOrgid",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(25),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
        {
            label: "分支编码",
            id: "custBranchid",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(25),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
    ];
};
const getUpdateFormFields = () => {
    return [
        {
            label: "用户名称",
            id: "userName",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
