                },
            ],
            component: SelectOption(dict.accountStatus, {
                placeholder: "请选择",
                // allowClear: true,
                style: {
                    width: 400,
                },
            }),
        },
        {
            label: "资金账户",
            id: "assetAccount",
            initialValue: "",
            rules: [
                {
                    required: true,
                    message: "参数不能为空",
                },
                {
                    validator: checkLength(25),
                    trigger: ["change", "blur"],
                },
            ],
            component: <Input placeholder="请输入" />,
        },
